<template>
    <div style="height:100%">
        <div class="container">
            <div class="content" style="padding: 112px 0 24px; flex:1 1 0%">
                <div class="top c">
                    <div class="header">
                        <a href="https://github.com/0604hx/vue-iview-starter">
                            <img alt="logo" class="logo" src="data:image/svg+xml;base64,">
                            <span class="title">vue-iview</span>
                        </a>
                    </div>
                    <div class="desc">
                        本页面模仿 <a href="https://github.com/ant-design/ant-design-pro" target="_blank">ant-design-pro</a> 的登录页面
                    </div>
                </div>
                <div style="width: 368px;margin: 0 auto;">
                    <div class="login">
                        <i-form ref="accountForm" :model="account" :rules="ruleValidate" :label-width="0" slot="left">
                            <Form-item prop="name">
                                <i-input v-model="account.name" icon="ios-person-outline" placeholder="请输入姓名">
                                    <!-- <Icon type="ios-person-outline" slot="prepend"></Icon> -->
                                </i-input>
                            </Form-item>
                            <Form-item prop="password" style="margin-bottom: 16px;">
                                <i-input v-model="account.password" type="password" icon="ios-locked-outline" placeholder="请输入登录密码">
                                    <!-- <Icon type="ios-locked-outline" slot="prepend"></Icon> -->
                                </i-input>
                            </Form-item>
                            <Form-item style="margin-bottom: 5px;">
                                <Checkbox v-model="account.remember">
                                    记住我
                                    <span class="h ml10">方便下次自动登录</span>
                                </Checkbox>
                            </Form-item>
                            <i-button type="primary" size="large" long :loading="working" @click.native="handleSubmit()">登录</i-button>
                        </i-form>
                    </div>
                </div>
            </div>
        </div>
    </div>
</template>
<style scoped>
    .loginM .ivu-modal-header-inner {color:#3399ff;text-align:center;font-weight: normal}

