<script>
    let NEXT = "demo"

    export default{
        data(){
            return{
                account:{
                    name:'',
                    password:'',
                    remember:false
                },
                ruleValidate: {
                    name: [
                        { required: true, message: '姓名不能为空', trigger: 'blur' },
                        { type: 'string', min: 2, message: '登录名不能少于2个字', trigger: 'blur' }
                    ],
                    password: [
                        { required: true, message: '密码不能为空', trigger: 'blur' },
                        { type: 'string', min: 5, message: '密码不能少于5个字', trigger: 'blur' }
                    ]
                },
                show:true,
                working:false
            }
        },
        created (){
        },
        methods:{
            handleSubmit() {
                this.$refs.accountForm.validate(valid => {
                    if (valid) {
                        this.$router.replace({name:NEXT})
                        M.notice.ok("在这里编写您的登录逻辑（默认跳转到 /demo)","登录成功")
                    } else {
                        M.warn("请填写登录信息")
                    }
                })
            },
            handleReset () {
                this.$refs.accountForm.resetFields();
            }
        },
        destroyed () {
            /**
             * add on 2017年12月19日10:08:31
             * 测试发现，vue-router 改变了 url 但并未切换页面
             * 这里进行强制刷新
             * 
             * 可能是 vue 版本问题
             */ 
            if(this.$route.name==NEXT)
                window.location.reload()
        }
    }
</script>
